import { quintOut } from 'svelte/easing'
import { crossfade } from 'svelte/transition'

const [send, receive] = crossfade({
  duration: d => Math.sqrt(d * 500),
  fallback(node, params) {
    const delay = params && params.in ? 300 : 0

    return {
      delay,
      duration: 600,
      easing: quintOut,
      css: t => `
                opacity: ${t}
            `
    }
  }
})

export { send, receive }
